// Read Course data to node
let DataArray = [];
let index = 0;
let row_width = innerWidth/8 - 10;
let col_width = 150;
console.log(screen);
for(let i=0; i<8; ++i){
    let j = 0;
    data_json[i].forEach(element => {
        let loc = "";
        loc = loc + row_width*i + " " + col_width*j;
        let data_obj = {};
        Object.defineProperties(data_obj, {
            "key": {
                value: index
            },
            "CourseName": {
                value: element
            },
            "loc": {
                value: loc
            },
            "IsClicked": {
                value: false,
                writable: true
            },
            "LinkFrom" : {
                value: [],
                writable: true
            },
            "LinkTo" : {
                value: [],
                writable: true
            },
            "CreditType" : {
                value: 0,
                writable: true
            },
            "Credits" : {
                value: 0,
                writable: true
            },
        });
        DataArray.push(data_obj);
        ++index;
        ++j;
    });
}

// Read relation data to node
let LinkArray = [];
data_json[8].forEach(element1 => {
    let index = 0;
    element1.forEach(element2 => {
        if(element2 == 1){
            let link_obj = {};
            Object.defineProperties(link_obj, {
                "from": {
                    value: index // column index

                },
                "to": {
                    value: data_json[8].indexOf(element1) // row index
                }
            });
            LinkArray.push(link_obj);

            DataArray[data_json[8].indexOf(element1)].LinkFrom.push(index);
            DataArray[index].LinkTo.push(data_json[8].indexOf(element1));

        }
        ++index;
    });
});
// console.log(LinkArray);
for(let i=0; i<index; ++i){
    if(data_json[9][i] == 0){
        DataArray[i].CreditType = 0;
        total_credit1 += data_json[10][i];
    }
    else{
        DataArray[i].CreditType = 1;
        total_credit2 += data_json[10][i];
    }
    DataArray[i].Credits = data_json[10][i];
}
information.childNodes[0].nodeValue = `必修：${credit1} / ${total_credit1} 選修:${credit2} / ${total_credit2} 總學分:${total}`;
console.log(DataArray);


// Create and Initialize canvas
let Canvas = objGo(go.Diagram, "Canvas",
{
    "undoManager.isEnabled": true, // Ctrl+Z or Ctrl+Y
    "commandHandler.copiesTree": true, // Ctrl+C or Ctrl+V
    "commandHandler.deletesTree": true, // Delete
    initialContentAlignment: go.Spot.Center,
    allowZoom: false, // Not to scale
});
Canvas.isReadOnly = true;
Canvas.allowHorizontalScroll = false,
Canvas.allowSelect = false;

// Set Node Skin
Canvas.nodeTemplate = objGo(go.Node, "Vertical",
    new go.Binding("location", "loc", go.Point.parse),

    objGo(go.Shape, "circle",
    {
        name: "SHAPE",
        width: 30,
        height: 30,
        fill: "lightblue",

    }),
    objGo(go.TextBlock, "default text",
    {
        width: 120,
        margin: 10,
        stroke: "black",
        font: "bold 20px sans-serif",
        textAlign: "center",
    },
    new go.Binding("text", "CourseName")),
    {
        cursor: "pointer",
        fromSpot: go.Spot.Right, // right out left in
        toSpot: go.Spot.Left,
        click: click_node,
        mouseEnter: mouseEnter_node,
        mouseLeave: mouseLeave_node,

    },
);
// Set Link Skin
Canvas.linkTemplate = objGo(go.Link,
    objGo(go.Shape,
    {
        name: "SHAPE",
        strokeWidth: 3,
        stroke: "#555",
        mouseEnter: mouseEnter_link,
        mouseLeave: mouseLeave_link,
    }),
    objGo(go.Shape,   // the arrowhead
    {
        toArrow: "Triangle",
    }),
    {
        routing: go.Link.AvoidsNodes, // Link path
        corner: 5, // rounded corners
        curve: go.Link.JumpGap,
        // curve: go.Link.JumpOver,
    });
// Create and Initialize model
let Diagram = objGo(go.GraphLinksModel); // Create model
Diagram.nodeDataArray = DataArray;// Create element
Diagram.linkDataArray = LinkArray;// Create link
Canvas.model = Diagram; // build Diagram

function click_node(e, node){ // change color when clicking node
    let shape = node.findObject("SHAPE");
    if(!node.data.IsClicked){
        if(node.data.LinkFrom.length != 0){
            // console.log(node.data.LinkFrom);
            let len= node.data.LinkFrom.length;
            for(let i=0; i<len; ++i){
                if(!DataArray[node.data.LinkFrom[i]].IsClicked){
                    alert(`${DataArray[node.data.LinkFrom[i]].CourseName} is not selected !!!`);
                    return;
                }
            }
        }
        node.data.IsClicked = true;
        shape.fill = "green";
        if(node.data.CreditType == 0)
            credit1 += node.data.Credits;
        else
            credit2 += node.data.Credits;
    }
    else{
        Clear_Click(node);
        node.data.IsClicked = false;
        shape.fill = "lightblue";
        if(node.data.CreditType == 0)
            credit1 -= node.data.Credits;
        else
            credit2 -= node.data.Credits;
    }
    total = credit1 + credit2;
    information.childNodes[0].nodeValue = `必修：${credit1} / ${total_credit1} 選修:${credit2} / ${total_credit2} 總學分:${total}`;
}
function mouseEnter_node(e, node){  // change node color when mouse hover
    let shape = node.part.findObject("SHAPE");
    if (shape.fill != "green")
        shape.fill = "red";
}
function mouseLeave_node(e, node){  // restore to original color
    let shape = node.part.findObject("SHAPE");
    if (shape.fill == "red")
        shape.fill = "lightblue";
}
function mouseEnter_link(e, node){
    node.stroke = "red";
    node.strokeWidth =  5;
}
function mouseLeave_link(e, node){
    node.stroke = "#555";
    node.strokeWidth =  3;
}
function Clear_Click(node){
    let iterator = node.findTreeChildrenNodes().iterator;
    // console.log(iterator.value.data.CourseName);
    // console.log(iterator.next().value.data.CourseName);
    if(!iterator.next())
        return;

    while(true){
        Clear_Click(iterator.value);
        let shape = iterator.value.findObject("SHAPE");
        if(shape.fill == "green" && iterator.value.data.CreditType == 0)
            credit1 -= iterator.value.data.Credits;
        if(shape.fill == "green" && iterator.value.data.CreditType == 1)
            credit2 -= iterator.value.data.Credits;
        iterator.value.data.IsClicked = false;
        shape.fill = "lightblue";
        if(!iterator.next())
            return;
    }

}
